dat_sample_averages <- dat_items %>%
  filter(question == "effect", time == "Pre") %>%
  group_by(effect) %>%
  summarise(
    Percentage = mean(response, na.rm = TRUE) * 100
  ) %>%
  ungroup()

ggplot(dat_sample_averages, aes(y = Percentage, x = effect)) +
